import React from 'react';
import { useState } from 'react';
import './Profile.css';

//Variable de arreglo de objetos
const Users = [
    {
        name: "David",
        age: 24,
        img: "https://images.pexels.com/photos/14358787/pexels-photo-14358787.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1"
    },
    {
        name: "Pedro",
        age: 23,
        img: "https://images.pexels.com/photos/14358783/pexels-photo-14358783.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1"
    },
    {
        name: "Fernanda",
        age: 27,
        img: "https://images.pexels.com/photos/4119004/pexels-photo-4119004.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1"
    }
]


const [people, setPeople] = useState('[]');

//funcion primaria
function Avatar() {
     Users.map((data) => {
      people.push(
      <div>
      <img className="avatar" width={90} height={90} src={data.img} alt={data.name} />
      <h3>{data.name}</h3>
      <h3>{data.age}</h3>
      </div>
      )
    })

    return (
        <div>
          {setPeople}
        </div>
      );
}


//Segunda funcion que se exporta con nombre de Profile que dentro retorna el componente Avatar
export default function Profile() {
    return (

      <Avatar
      />
    );
}
